import React from 'react'
import styled from 'styled-components'
import Mesaje from '../components/Mesaje';
import Navbar from '../components/Navbar';
import Newsletter from '../components/Newsletter';
import Footer from '../components/Footer';
import { Add, Remove } from '@material-ui/icons';

const Container = styled.div``;

const Wrapper = styled.div`
padding: 50px; 
display: flex;
`;

const ImageContainer = styled.div`
flex: 1;
`;

const Image = styled.img`
width: 100%;
object-fit: cover;
`;


const InfoContainer = styled.div`
flex: 1;
padding: 0px 50px;
`;

const Title = styled.h1`
font-weight: 150;
`;

const Description = styled.div`
margin: 20px 0px;
`;

const Price = styled.span`
font-size: 30px;
font-weight: 80;
`;

const FilterContainer = styled.div`
display: flex;
width: 50%;
margin: 30px 0px;
`;

const Filter = styled.div`
display: flex;
align-items: center;
`;

const FilterTitle = styled.span`
font-size: 20px;
font-weight: 100;
`;

const FilterColor = styled.div`
width: 20px;
height: 20px;
border-radius: 50%;
background-color: ${props => props.color};
cursor: pointer;
margin: 0 5px;
`;

const AddContainer = styled.div`
width: 50%;
display: flex;
align-items: center; 
justify-content: space-between;
`;

const QuantityContainer = styled.div`
display: flex;
align-items: center;
font-weight: 400;
`;

const Quantity = styled.span`
width: 30px;
height: 30px;
display: flex;
align-items: center;
justify-content: center;
margin: 0 4px;
border-radius: 10px;
border: 2px solid lightgreen;
`;

const Button = styled.button`
border: 2px solid lightgreen;
font-weight: 400;
padding: 10px;
background-color: white;
cursor: pointer;
`;


const Product = () => {
    return (
        <Container>
            <Mesaje />
            <Navbar />
            <Wrapper>
                <ImageContainer>
                    <Image src="./thor125a.jpg" />
                </ImageContainer>
                <InfoContainer>
                    <Title> BARTON THOR 125cc</Title>
                    <Description>MOTOR – 4T, monocilindric
                        CAPACITATE – 125 cm 3
                        APRINDERE – electrica la buton
                        PUTERE MAXIMĂ – 5,2kW / 8.500 rpm
                        TRANSMISIE – automata
                        DIMENSIUNI SI GREUTATE
                        LUNGIME: 1530 mm
                        ÎNĂLȚIME: 980 mm
                        Lațime: 930 mm
                        Înălțimea scaunului: 700 mm
                        Ampatament: 950 mm
                        Greutate : 92 kg


                        JANTE FATA: Otel 8”
                        FRANA FATA: tambur
                        JANTE SPATE: Oțel 8”
                        FRANA SPATE: Disc, hidraulica</Description>
                    <Price> 5800 lei </Price>
                    <FilterContainer>
                        <Filter>
                            <FilterTitle>Color</FilterTitle>
                            <FilterColor color="rosu" />
                            <FilterColor color="negru" />
                            <FilterColor color="albastru" />
                            <FilterColor color="alb" />
                            <FilterColor color="galben" />
                            <FilterColor color="verde" />
                        </Filter>
                    </FilterContainer>
                    <AddContainer>
                        <QuantityContainer>
                            <Remove />
                            <Quantity>1</Quantity>
                            <Add />
                        </QuantityContainer>
                        <Button>ADAUGA IN COS</Button>
                    </AddContainer>
                </InfoContainer>
            </Wrapper>
            <Newsletter />
            <Footer />
        </Container>
    )
}

export default Product
